import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import { Fade } from "react-reveal";
import styles from './style.module.scss'


const footerMenu = [
    {
        title: 'COURSES',
        menu: ['Blockchain', 'Hyperledger', 'Ethereum', 'Artificial', 'Intelligence']
    },
    {
        title: 'TRAINERS',
        menu: ['Aman Sanduja', 'Ashish Kots', 'Ankur Bist']
    },
    {
        title: 'PARTNERS',
        menu: ['MSME', 'Bennett University', 'Vishkarma University', 'Nine Pages']
    },
]
const Footer = () => {
    return (
        <div>
            <div className={styles.footerTop}>
                <Container>
                    <Col md="12" lg={{ size: 10, offset: 1 }}>
                    <Row>
                        {footerMenu.map((menu) => (
                            <Col xs="12" sm="6" md="6" lg="3" key={menu.title}>
                                <Fade bottom cascade>
                                <h2>{menu.title}</h2>
                                {menu.menu.map((sub) => (
                                    <p>{sub}</p>
                                ))}
                                </Fade>
                            </Col>
                        ))}
                        <Col xs="12" sm="6" md="6" lg="3">
                        <Fade bottom cascade>
                            <div className={styles.fourthContainer}>
                                <div className={styles.dot}></div>
                                <div className={styles.dotRight}>
                                    <div>Sec 44, Plot 126, Gurugram</div>
                                </div>
                            </div>  
                            <div className={styles.fourthContainer}>
                                <div className={styles.dot}></div>
                                <div className={styles.dotRight}>
                                <div>contact@blockchainyatra.com</div>
                                <div>aman@blockchainyatra.com</div>
                                </div>
                            </div>
                            <div className={styles.fourthContainer}>
                                <div className={styles.dot}></div>
                                <div className={styles.dotRight}>
                                <div>(229) 469-6358</div>
                                <div>(226) 390-5707</div>
                                </div>
                            </div>
                            </Fade>
                        </Col>
                    </Row>
                    </Col>
                </Container>
            </div>
            <div className={styles.footerBottom}>
                <h2>@copy2019 Blockchain Yatra . All Rights Reserved. Designed by Blockchain Yatra</h2>
            </div>
        </div>
    );
};

export default Footer;
