import React from 'react';
import cx from 'classnames';

import Card from '../Card';
import NameSurname from './NameSurname';
import Arrow from './Arrow';
import ToggledCard from './ToggledCard';

import styles from './styles.scss';

class DropdownCard extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      arrowToggled : false
    };
  }

  toggleArrow() {
    this.setState(prevState => {
      return { arrowToggled: !prevState.arrowToggled };
    });
  }

  render(){
    let{
      className,
      name,
      surname,
      email,
      phone,
      info,
      messageOnToggled,
      messageOnUntoggled,
      buttonColor,
      bgColor,
      ...other
    } = this.props;

    return(
      <Card
        className={cx(
          styles['dropdown-card']
        )}
        {...other}
      >
        <NameSurname
          name={ name }
          surname={ surname }
          bgColor={ bgColor }
        />

        {
          this.state.arrowToggled === true
            ? <React.Fragment>                 
                <button
                  className={styles.button}
                  style={{
                    backgroundColor : buttonColor
                  }}
                >
                  <Arrow
                    onClick={this.toggleArrow.bind(this)}
                    position={ this.state.arrowToggled }
                    message={ messageOnUntoggled }
                  />
                </button>

                <ToggledCard
                  email={ email }
                  phone={ phone }
                  info={ info }
                  bgColor={ bgColor }
                />
              </React.Fragment>
          : <button
                                          className={styles.button}
                            style={{
                              backgroundColor : buttonColor
                            }}
               >
                 <Arrow
                   onClick={this.toggleArrow.bind(this)}
                   position={ this.state.arrowToggled }
                   message={ messageOnToggled }
                 />
               </button>
        }

      </Card>
    );
  }
}
export default DropdownCard;
